def w_sum(input , weigh):
  assert(len(input) == len(weigh))
  out = 0
  for i in range(len(input)):
    if( input[i] != input[i]):
      print("INPUT NAN")
      exit()
    out  = out +( input[i] * weigh[i])
  return out


def nerual_network(input , weigh):
  return (w_sum(input , weigh) )

